  CPPUNIT_ASSERT(cred_ok);

  // both username and password are given: basic credential check is ok.
  cred_ok = user->CheckUserCredentials();
  CPPUNIT_ASSERT(cred_ok);

  pwd = "";
  cred_ok = user->SetCredentials(usr, pwd, proxyFlag, proxyHost,proxyPort, r_me);
  CPPUNIT_ASSERT(cred_ok);

