    setUser(dispatch, payload);
    dispatch(isProcessing(false));
    toast('success', message);
  } catch (error) {
    const { message } = error.response.data;
    dispatch(userAuthFailure(message));
    dispatch(isProcessing(false));
    toast('error', message);
  }
};

