{
    $email = $request->email;
    $user  = User::where('email', $email)->first();

    if ($user) {
        $string = strtolower(str_random(6));


        $this->mailer->send('hrms.auth.reset_password', ['user' => $user, 'string' => $string], function ($message) use ($user) {
