async fn assert_request(port: u16) {
    let http_client = fuchsia_hyper::new_client();
    let request_uri = Uri::builder()
        .scheme("http")
        .authority(format!("[::1]:{:?}", port).as_str())
        .path_and_query("/")
        .build()
        .unwrap();
    let response = http_client.get(request_uri).await.unwrap();
