    beforeAll(async () => {
        client = { prisma: prismaGenerator.createPrismaClient() };
        await createTestUser(client, testUser as never);
    });

    afterAll(async () => {
        await removeTestUser(client, testUser as never);
    });

    it('invalid username or password pattern', async () => {
        const result = await request(app).post(path).send({
            email: testUser.email,
            password: 'invalidPassword',
        });
        expect(JSON.parse(result.text)).toEqual({
            message: 'invalidInput',
        });
        expect(result.statusCode).toEqual(422);
    });

    it('incorrect username or password', async () => {
        const result = await request(app).post(path).send({
            email: testUser.email,
            password: 'TestUser2password!',
        });
        expect(JSON.parse(result.text)).toEqual({
            message: 'emailOrPasswordInvalid',
        });
        expect(result.statusCode).toEqual(401);
    });

